Object.Field:
/* This temporary table is used for the "Smart Install" feature	*/
/* It stores the list of the tables that will be part of the CDC Set	*/
/* Then, it is compared with CDC Objects table to determine wich	*/
/* action should be performed (create or drop Journals, etc.)	*/
create table <%=snpRef.getObjectName("L" ,"SNP_TMP_TABLE_LIST" , "W")%>
(
	FULL_TABLE_NAME	<%=snpRef.getDataType("DEST_VARCHAR", "255")%> not null
)

/* A single Oracle subscription is used. This has no link at all with the Sunopsis subscribers */
DECLARE
	subhandle NUMBER;
BEGIN
	DBMS_LOGMNR_CDC_SUBSCRIBE.GET_SUBSCRIPTION_HANDLE(
	     change_set	=> 'SYNC_SET',
	     description	=> '<%=snpRef.getJrnInfo( "JRN_COD_MOD" )%>',
	     subscription_handle	=> subhandle
	);
END;

/* The name of the subscriber view is suffixed with the schema name of the journliazed table */
/* This allows to journalize the same table in different schemas			 */


BEGIN
	DBMS_LOGMNR_CDC_SUBSCRIBE.SUBSCRIBE(
	     subscription_handle => to_number('#SUBHANDLE'),
	     source_schema => '<%=snpRef.getInfo("DEST_SCHEMA")%>',
	     source_table => '<%=snpRef.getJrnInfo("TABLE_NAME")%>',
	     column_list => '<%=snpRef.getColList("", "[COL_NAME]", ",", "", "PK")%>'
	);
END;

/* We perform this step once we successfully passed all the steps used to configure/check the Oracle instance */
/* When this step will be executed, AUTO_CONFIGURATION will no longer be executed for this CDC Set */
insert into <%=snpRef.getJrnInfo("CDC_SET_TABLE")%>
(
	CDC_SET_NAME,
	CUR_WINDOW_ID,
	CUR_WINDOW_ID_DEL,
	CUR_WINDOW_ID_INS,
	RETRIEVE_DATA,
	REFRESH_ROW_COUNT
)
values
(
	/* CDC_SET_NAME */		'<%=snpRef.getObjectName("L" ,snpRef.getJrnInfo( "JRN_COD_MOD" ) , "D")%>',
	/* CUR_WINDOW_ID */		-1,
	/* CUR_WINDOW_ID_DEL */	-1,
	/* CUR_WINDOW_ID_INS */	-1,
	/* RETRIEVE_DATA */		NULL,
	/* REFRESH_ROW_COUNT */	'update <%=snpRef.getJrnInfo("CDC_SUBS_TABLE")%> set CDC_ROW_COUNT = 0 where CDC_SUBSCRIBER = ''$$SUBSCRIBER_NAME$$'' and CDC_SET_NAME = ''<%=snpRef.getObjectName("L" ,snpRef.getJrnInfo( "JRN_COD_MOD" ) , "D")%>'''
)

/* Each time the Change Set is modified, it must be disabled	*/
/* And to disable a change set, subscription must be dropped	*/
BEGIN
	DBMS_LOGMNR_CDC_SUBSCRIBE.DROP_SUBSCRIPTION(
	     subscription_handle	=> to_number('#SUBHANDLE')
	);
END;

/* This tables stores the journalizing items (table, views, triggers) created.	*/
/* It is used for the "smart install" feature.			*/
create table <%=snpRef.getJrnInfo("CDC_OBJECTS_TABLE")%>
(
	FULL_TABLE_NAME	<%=snpRef.getDataType("DEST_VARCHAR", "255")%> not null,	/* Full name of the journalized table */
	CDC_OBJECT_TYPE	<%=snpRef.getDataType("DEST_VARCHAR", "35")%> not null,	/* Object type in the CDC framework */
	FULL_OBJECT_NAME	<%=snpRef.getDataType("DEST_VARCHAR", "255")%> not null,	/* Full name of the object */
	DB_OBJECT_TYPE	<%=snpRef.getDataType("DEST_VARCHAR", "35")%> null,	/* Object RDBMS type (ex : TABLE, VIEW, TRIGGER) */
	constraint PK_SNP_CDC_OBJ primary key (FULL_TABLE_NAME, CDC_OBJECT_TYPE)
)

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.			*/

alter user <%=snpRef.getInfo("DEST_USER_NAME")%>
quota unlimited on SYSTEM

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT CREATE SESSION TO <%=snpRef.getInfo("DEST_USER_NAME")%>

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT CREATE TABLE TO <%=snpRef.getInfo("DEST_USER_NAME")%>

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT SELECT_CATALOG_ROLE TO <%=snpRef.getInfo("DEST_USER_NAME")%>

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT EXECUTE_CATALOG_ROLE TO <%=snpRef.getInfo("DEST_USER_NAME")%>

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT SELECT ANY TABLE TO <%= snpRef.getInfo("DEST_USER_NAME") %>

/* After each change on the content of this table we recompute the statistics	*/
/* Statistics are always up to date on this table			*/

begin
    dbms_stats.gather_table_stats(
	ownname => '<%=odiRef.getSchemaNameDefaultPSchema( "W" )%>',
	tabname => '<%=snpRef.getJrnInfo("CDC_SUBS_TABLE").replaceAll("","").replaceAll("..>",".replaceAll(snpRef.getSchemaNameDefaultPSchema(\\u0022W\\u0022)+\\u0022.\\u0022,\\u0022\\u0022)?>")%>',
	estimate_percent => dbms_stats.auto_sample_size
    );
end;

/* After each change on the content of this table we recompute the statistics	*/
/* Statistics are always up to date on this table			*/

begin
    dbms_stats.gather_table_stats(
	ownname => '<%=odiRef.getSchemaNameDefaultPSchema( "W" )%>',
	tabname => '<%=snpRef.getJrnInfo("CDC_TABLE_TABLE").replaceAll("","").replaceAll("..>",".replaceAll(snpRef.getSchemaNameDefaultPSchema(\\u0022W\\u0022)+\\u0022.\\u0022,\\u0022\\u0022)?>")%>',
	estimate_percent => dbms_stats.auto_sample_size
    );
end;

/* After each change on the content of this table we recompute the statistics	*/
/* Statistics are always up to date on this table			*/

begin
    dbms_stats.gather_table_stats(
	ownname => '<%=odiRef.getSchemaNameDefaultPSchema( "W" )%>',
	tabname => '<%=snpRef.getJrnInfo("CDC_SET_TABLE").replaceAll("","").replaceAll("..>",".replaceAll(snpRef.getSchemaNameDefaultPSchema(\\u0022W\\u0022)+\\u0022.\\u0022,\\u0022\\u0022)?>")%>',
	estimate_percent => dbms_stats.auto_sample_size
    );
end;

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT SELECT ANY TABLE TO <%= snpRef.getInfo("DEST_WORK_SCHEMA") %>

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT CONNECT TO <%=snpRef.getInfo("DEST_USER_NAME")%>

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

GRANT RESOURCE TO <%=snpRef.getInfo("DEST_USER_NAME")%>

/* This statement will be executed only the first time the journal is created.	*/
/* Afterwards, It is ignored thanks to the source query.		*/

/*	If this step generates an ORA-04031, you must set it manually with the command :	*/
/*		ALTER SYSTEM SET JAVA_POOL_SIZE=50000000 SCOPE=SPFILE	*/
/*	and restart the database						*/
ALTER SYSTEM SET JAVA_POOL_SIZE=50000000 SCOPE=BOTH

/* This query will return one row, if the CDC Set is not already declared	*/
/* and if the JAVA_POOL_SIZE parameter is less than the recommended value.	*/
/* The recommended value is 50M				*/
select	'X'
from	V$PARAMETER
where	upper(name) = 'JAVA_POOL_SIZE'
and	to_number(value) < 50000000
and	not exists (
	select	'X'
	from	<%=snpRef.getJrnInfo("CDC_SET_TABLE")%>
	where	CDC_SET_NAME = '<%=snpRef.getObjectName("L" ,snpRef.getJrnInfo( "JRN_COD_MOD" ) , "D")%>'
	)

insert into <%=snpRef.getJrnInfo("CDC_TABLE_TABLE")%>
(
	CDC_SET_NAME,
	FULL_TABLE_NAME,
	FULL_DATA_VIEW,
	RETRIEVE_DATA,
	REFRESH_ROW_COUNT
)
values
(
	/* CDC_SET_NAME */		'<%=snpRef.getObjectName("L" ,snpRef.getJrnInfo( "JRN_COD_MOD" ) , "D")%>',
	/* FULL_TABLE_NAME */		'<%=snpRef.getJrnInfo("FULL_TABLE_NAME")%>',
	/* FULL_DATA_VIEW */		'<%=snpRef.getJrnInfo("JRN_FULL_DATA_VIEW")%>',
	/* RETRIEVE_DATA */		NULL,
	/* REFRESH_ROW_COUNT */	'update <%=snpRef.getJrnInfo("CDC_SUBS_TABLE")%> SUBS set SUBS.CDC_ROW_COUNT = (select SUBS.CDC_ROW_COUNT + count(*) from <%=snpRef.getJrnInfo("JRN_FULL_NAME")%> JRN where JRN.CSCN$ > SUBS.MIN_WINDOW_ID) where CDC_SET_NAME = ''<%=snpRef.getObjectName("L" ,snpRef.getJrnInfo( "JRN_COD_MOD" ) , "D")%>'' and CDC_SUBSCRIBER=''$$SUBSCRIBER_NAME$$'''
)
