/* Compute Window Id for each table of the CDC Set from parents to children */
/* This computes consistency regarding Deletes */
update	<%=odiRef.getJrnInfo("JRN_FULL_NAME")%> <%=odiRef.getInfo("DEST_TAB_ALIAS_WORD")%> JRN
set	JRN.WINDOW_ID =	(
			select	CDC.CUR_WINDOW_ID_DEL + 2
			from	<%=odiRef.getJrnInfo("CDC_SET_TABLE")%> <%=odiRef.getInfo("DEST_TAB_ALIAS_WORD")%> CDC
			where	CDC.CDC_SET_NAME = '<%=odiRef.getObjectName("L", odiRef.getJrnInfo("JRN_COD_MOD"), "D")%>'
			)
where	JRN.WINDOW_ID is null

