Object.Field:
triggerCmd = """ create or replace trigger <%=odiRef.getJrnInfo("JRN_FULL_TRIGGER")%> after insert or update or delete on <%=odiRef.getJrnInfo("FULL_TABLE_NAME")%> for each row declare V_FLAG VARCHAR(1); <%=odiRef.getColList("", "V_[COL_NAME]\t[DEST_CRE_DT];", "\n\t", "", "PK")%> begin if inserting then <%=odiRef.getColList("", "V_[COL_NAME] := :new.[COL_NAME];", "\n\t\t", "", "PK")%> V_FLAG := 'I'; end if;
if updating then <%=odiRef.getColList("", "V_[COL_NAME] := :new.[COL_NAME];", "\n\t\t", "", "PK")%> V_FLAG := 'I'; end if;
if deleting then <%=odiRef.getColList("", "V_[COL_NAME] := :old.[COL_NAME];", "\n\t\t", "", "PK")%> V_FLAG := 'D'; end if;
insert into <%=odiRef.getJrnInfo("JRN_FULL_NAME")%> ( JRN_SUBSCRIBER, JRN_CONSUMED, JRN_FLAG, JRN_DATE, <%=odiRef.getColList("", "[COL_NAME]", ",\n\t\t", "", "PK")%> ) select JRN_SUBSCRIBER, '0', V_FLAG, sysdate, <%=odiRef.getColList("", "V_[COL_NAME]", ",\n\t\t", "", "PK")%> from <%=odiRef.getJrnInfo("SNP_JRN_SUBSCRIBER")%> where JRN_TNAME = '<%=odiRef.getJrnInfo("FULL_TABLE_NAME")%>' /* The following line can be uncommented for symetric replication */ /* and upper(USER) <> upper('<%=odiRef.getInfo("DEST_USER_NAME")%>') */ ; end; """
# Create the statement myStmt = myCon.createStatement()
# Execute the trigger creation myStmt.execute(triggerCmd)
myStmt.close() myStmt = None
