Object.Field:
import re

# Init Vars
nbRows = 0
nbIns = 0
nbBad = 0
nbDis = 0
strprt = ""
c = 0

# Open NZLoad log file
src_file  = "<%=snpRef.getSrcTablesList("", "[RES_NAME]", "", "")%>"
temp_dir2 = "<?=java.lang.System.getProperty("java.io.tmpdir")?>"
os_name = "<?=java.lang.System.getProperty("os.name")?>"
if os_name == "Windows":
   temp_dir = temp_dir2
else:
   temp_dir = temp_dir2 + "/"

output_dir = "<%=odiRef.getOption("OUTPUT_DIR")%>"
log_file = "<%=odiRef.getOption("LOG_FILE")%>"

if output_dir == "":
   output_dir = temp_dir
if log_file == "":
   log_file = output_dir + src_file + '.log'

f = open(log_file, "r")

try:
        lines = f.readlines()

        while c < len(lines):
                line = lines[c]
                if line.lstrip().startswith("number of records read:"):
                        nbRows = int(re.findall("\d+", line)[0])
                if line.lstrip().startswith("number of bad records:"):
                        nbBad = int(re.findall("\d+", line)[0])
                if line.lstrip().startswith("number of discarded records:"):
                        nbDis = int(re.findall("\d+", line)[0])
                if line.lstrip().startswith('number of records loaded:'):
                        nbIns = int(re.findall("\d+", line)[0])
                c+=1
        strprt = "\n\tReads:\t%s\n\tIns:\t%s\n\tBad:\t%s\n\tDiscarded:\t%s\n" % (nbRows, nbIns, nbBad, nbDis)
        print strprt
        odiRef.setNbInsert(nbIns);
        odiRef.setNbRows(nbRows);
        odiRef.setNbErrors(nbBad);

finally:
        f.close()
