import re

# Init Vars
nbIns = 0
nbErr1 = 0
nbErr2 = 0
nbDup = 0
nbIns = 0
nbUpd = 0
nbDel = 0
nbRej = 0
strprt = ""
c = 0

# Open TTU log file
f = open(r"<%=odiRef.getOption("TEMP_DIR")%>/<%=odiRef.getTable("L", "TARG_NAME", "W")%>_<%=odiRef.getInfo("SRC_SET_NAME")%>.log", "r") 
try:
	lines = f.readlines()

<% if (odiRef.getOption( "TERADATA_UTILITY" ).equals( "FASTLOAD" )) { %>

	while c < len(lines):
		line = lines[c]
		if line.lstrip().startswith("Total Error Table 1"):
			nbErr1 = int(re.findall("\d+", line)[1])
		if line.lstrip().startswith("Total Error Table 2"):
			nbErr2 = int(re.findall("\d+", line)[1])
		if line.lstrip().startswith('Total Inserts Applied'):
			nbIns = int(re.findall("\d+", line)[0])
		if line.lstrip().startswith('Total Duplicate Rows'):
			nbDup = int(re.findall("\d+", line)[0])
		c+=1
	strprt = "\n\tIns:\t%s\n\tErr1:\t%s\n\tErr2:\t%s\n\tDup:\t%s" % (nbIns, nbErr1, nbErr2, nbDup)
	odiRef.setNbRows(nbIns);

<% }; if (odiRef.getOption( "TERADATA_UTILITY" ).equals( "MLOAD" )) { %>

	while c < len(lines):
		line = lines[c]
		if line.lstrip().startswith('Candidate records rejected'):
			nbRej = int(re.findall("\d+", line)[0])
		if line.lstrip().startswith('Inserts:'):
			nbIns = int(re.findall("\d+", line)[0])
		if line.lstrip().startswith('Updates:'):
			nbUpd = int(re.findall("\d+", line)[0])
		if line.lstrip().startswith('Deletes:'):
			nbDel = int(re.findall("\d+", line)[0])
		if line.rstrip().endswith('Error table statistics for:'):
			nbErr1 = int(re.findall("\d+", lines[c+5])[0])
			nbErr2 = int(re.findall("\d+", lines[c+6])[0])
		c+=1
	strprt = "\n\tIns:\t%s\n\tUpd:\t%s\n\tDel:\t%s\n\tReject:\t%s\n\tErr1:\t%s\n\tErr2:\t%s" % (nbIns, nbUpd, nbDel, nbRej, nbErr1, nbErr2)
	odiRef.setNbRows(nbIns);

<% }; if (odiRef.getOption( "TERADATA_UTILITY" ).equals( "TPUMP" )) { %>

	while c < len(lines):
		line = lines[c]
		if line.lstrip().startswith('Candidate records rejected'):
			nbRej = int(re.findall("\d+", line)[0])
		if line.lstrip().count('Statistics for Apply Label')>0:
			# Get all statistic lines
			list = []
			list.append( [lines[c+2].split()[0], lines[c+2].split()[3]] )
			if not(lines[c+3].startswith("****")):
				list.append( [lines[c+3].split()[0], lines[c+3].split()[3]] )
				if not(lines[c+4].startswith("****")):
					list.append( [lines[c+4].split()[0], lines[c+4].split()[3]] )
			# Store row counts in appropriate counters
			for e in list:
				if e[0]=="I":
					nbIns = int(e[1])
				if e[0]=="U":
					nbUpd = int(e[1])
				if e[0]=="D":
					nbDel = int(e[1])
		if line.lstrip().count("UTY0820 Error table")>0:
			if line.count(".ET_")>0:
				nbErr1 = int(re.findall("\d+", re.findall("contains \d+", line)[0])[0])
			if line.count(".UV_")>0:
				nbErr2 = int(re.findall("\d+", re.findall("contains \d+", line)[0])[0])
		c+=1
	strprt = "\n\tIns:\t%s\n\tUpd:\t%s\n\tDel:\t%s\n\tReject:\t%s\n\tErr1:\t%s\n\tErr2:\t%s" % (nbIns, nbUpd, nbDel, nbRej, nbErr1, nbErr2)
	odiRef.setNbRows(nbIns);

<% }; if (odiRef.getOption( "TERADATA_UTILITY" ).startsWith( "TPT" )) { %>

