import time import os import java.sql as js import java.lang as lg import java.util as ju import glob
class Log: """Creates a log file and traces information """ def __init__(self, logfilename, uselog=1, append=0, printtoscreen=0): self.logfilename = logfilename self.printtoscreen = printtoscreen self.uselog=uselog if self.uselog: if not append: # Reset the file f = open(logfilename, "w") f.close()
def record(self, rec): if self.uselog: t = time.localtime() l = '%d-%02d-%02d %02d:%02d:%02d - %s' % (t[0], t[1], t[2], t[3], t[4], t[5], rec) f = open(self.logfilename, "a+") print >> f, l f.close() if self.printtoscreen: print l
class DDXRecord: """ DDX Record container Class """ def __init__(self, name,type,length): self.name = name self.type = type self.length = length def asString(self): """ helper method that displays a record as a string """ return "DDXRecord: [name=%s, type=%s, length=%s]" %(self.name, self.type, self.length)
class DDXField: """ DDXField Container Class """ def __init__(self, name,type,startsincol,length, redefinesfield=None): self.name = name self.type = type self.startsincol=startsincol self.length = length self.redefinesfield = redefinesfield
def asString(self): """ helper method that displays a field as a string """ return "DDXField: %s REDEFINES(%s)\t\t%s\t(%d,%d)" %(self.name, self.redefinesfield, self.type, self.startsincol, self.length)
class DDXFileReader: def __init__(self, ddxfilename, tmpfile, logger): self.DdxFileName = ddxfilename self.RkmFileName = tmpfile self.Logger = logger self.XMLDriver = "com.sunopsis.jdbc.driver.xml.SnpsXmlDriver" # Notes: URL drops schema on disconnect until we have the valid DDL.xsd file # So far, we process every single XML file as a standalone file... self.XMLUrl = "jdbc:snps:xml?f=%s&s=DDXSCHEMA&ro=true&dod=true" %(self.RkmFileName) self.DDXRecord = None self.DDXFields = []
def open(self): """ Opens the DDX File using ODI's XML Driver """ c=open(self.DdxFileName, "r").read().replace('xsi:noNamespaceSchemaLocation="DDL.xsd"', '') f=open(self.RkmFileName, "w") f.write(c) f.close()
self.Logger.record('Registering Driver %s' % self.XMLDriver) xmldriver = lg.Class.forName(self.XMLDriver).newInstance() self.Logger.record('Connecting to source DDX file using URL %s' % self.XMLUrl) # self._xmlCx = js.DriverManager.getConnection(self.XMLUrl, "", "") self._xmlCx = xmldriver.connect(self.XMLUrl, ju.Properties()) self.Logger.record('Successfully opened file %s' % (self.RkmFileName)) # os.remove(self.RkmFileName)
def getDDXRecord(self): """ Returns a DDXRecord """ if not self.DDXRecord: sql = "select NAME, TYPE_, LENGTH_ from RECORD" # Get DDX record from XML table: RECORD self.Logger.record('Getting DDX record (executing sql: %s).' % sql) rs = self._xmlCx.createStatement().executeQuery(sql) rs.next() self.DDXRecord =DDXRecord(rs.getString("NAME"), rs.getString("TYPE_"), rs.getString("LENGTH_")) rs.close() self.Logger.record("done.") return self.DDXRecord
def getDDXFields(self): """ Returns a list of DDX Fields in the order in which they appear """ if self.DDXFields==[]: ListCol={} sql = "select NAME, TYPE_, STARTS_IN_COLUMN, LENGTH_ from FIELD order by FIELDORDER" self.Logger.record('Getting DDX fields (executing sql: %s).' % sql) rs = self._xmlCx.createStatement().executeQuery(sql) currentpos = 0 lastredefinedfield = "" while rs.next(): # workaround trillium: # 1. Some fields are added by TSQ without a name => ignore them # 2. Manage redefines: skip fields of which startpos is included in currentpos fldname = rs.getString("NAME") fldtype = rs.getString("TYPE_") length = rs.getLong("LENGTH_") start = rs.getLong("STARTS_IN_COLUMN") if (fldname and (ListCol.has_key(fldname) ==0)): ListCol[fldname]='1' if start >= currentpos: self.DDXFields.append(DDXField(fldname, fldtype, start, length)) currentpos = start + length lastredefinedfield = fldname else: self.DDXFields.append(DDXField(fldname, fldtype, start, length, lastredefinedfield)) rs.close() self.Logger.record("done.") return self.DDXFields
def close(self): self.Logger.record('Closing %s' % self.XMLUrl) if self._xmlCx: self._xmlCx.close() self.Logger.record('Connection closed.')
class DDXReverse: def __init__(self, iMod, ProjectDir, DDXPattern, repoConnect, logger, usefriendlynames=0, FileFormat='',recSep='',fieldSep=''): self.Logger = logger self.iMod = iMod SplitDir = os.path.split(ProjectDir)
if SplitDir[1]=='data': self.ProjectDir = SplitDir[0] self.DataNode = "" else: self.ProjectDir = ProjectDir self.DataNode = "data"+os.sep self.DDXDir = self.ProjectDir+os.sep+"ddl" self.DATDir = self.ProjectDir+os.sep+"data" self.TEMPDir = self.ProjectDir+os.sep+"tmp" self.Logger.record('\nDirectories %s \nDDL %s\n DATA %s\n TEMP %s\nDaTaNode %s' %( self.ProjectDir,self.DDXDir,self.DATDir,self.TEMPDir,self.DataNode) ) self.DDXPattern = DDXPattern
self.repoCx = repoConnect self.usefriendlynames = usefriendlynames self.fileFormat = fileFormat self.recSep = recSep self.fieldSep = fieldSep
# List of tables returned from the list of files # To avoid duplicate table (record) definitions in several DDX Files self.TableNames={}
def _str2Hex (self,s=''): if len(s)==0: return "" return "%X" %ord(s[0])+self._str2Hex(s[1:])
def startReverse(self): self.Logger.record('Reverse Started.') self.Logger.record('Retrieving file list for %s' % self.ProjectDir)
for fic in glob.glob(self.DDXDir + os.sep + DDXPattern): filename = os.path.split(fic)[1] shortname = os.path.splitext(filename)[0] tmpfile = self.TEMPDir+ os.sep+filename datafile = self.DataNode+shortname+'.dat'
self.Logger.record('Processing file %s' % fic) f = DDXFileReader(fic, tmpfile, self.Logger) f.open() try: self._writeTable(f, f.getDDXRecord(), f.getDDXFields(),datafile,fic) except: f.close() raise f.close() self.Logger.record('Done.')
def _writeTable(self, ddxFileName=None, ddxRecord=None, ddxFields=[],DataFileName="",fic=""): if ddxRecord: # skip reverse if a record of the same name has already been processed if not self.TableNames.has_key(ddxRecord.name[:128]): # Insert Table sql = """ insert into SNP_REV_TABLE( I_MOD, TABLE_NAME, RES_NAME, TABLE_ALIAS, TABLE_TYPE, TABLE_DESC, IND_SHOW, FILE_FORMAT, FILE_SEP_ROW, FILE_FIRST_ROW,FILE_SEP_FIELD) values( ?, ?, ?, ?, 'T', ?, '1',?, ?, '0',?) """
resname = DataFileName self.Logger.record(ddxRecord.asString()) ps = self.repoCx.prepareStatement(sql) ps.clearParameters() ps.setLong(1,self.iMod) ps.setString(2,ddxRecord.name[:128]) #ps.setString(3,ddxRecord.name[:250]) ps.setString(3,resname.replace('\\','/')) ps.setString(4,ddxRecord.name[:128]) ps.setString(5,("DDX Auto-Import by ODI\nOriginal DDX File: %s\nRecord: %s(%s)" % (fic, ddxRecord.name, ddxRecord.length))[:250]) ps.setString(6,self.fileFormat) ps.setString(7,self.recSep) ps.setString(8,self._str2Hex(self.fieldSep)) ps.executeUpdate() ps.close() # Add Columns sql = """insert into SNP_REV_COL(I_MOD, TABLE_NAME, COL_NAME, COL_HEADING, DT_DRIVER, FILE_POS,BYTES, LONGC, IND_WRITE, COL_MANDATORY, CHECK_FLOW, CHECK_STAT) values (?,?,?,?,'STRING',?,?,?,'1','0','0','0') """ ps = self.repoCx.prepareStatement(sql) for fld in ddxFields: self.Logger.record(fld.asString()) ps.clearParameters() ps.setLong(1, self.iMod) ps.setString(2,ddxRecord.name)
fldname = fld.name[:100] ps.setString(3,fldname) ps.setString(4,self._getFriendlyNameForField(fldname,self.usefriendlynames)[:35]) ps.setInt(5,fld.startsincol+1) ps.setInt(6,fld.length) ps.setInt(7,fld.length) ps.executeUpdate() ps.close() # Register new record processed self.TableNames[ddxRecord.name[:128]]=ddxRecord.name
def _getFriendlyNameForField(self,fldname=None,ok=0): """ returns a friendly name for a field For example, field LEV1_MATCHED_NUMBER_MEMBERS will be converted to "Lev1 Matched Number Members" """ if not(ok): return fldname retfldname = "" if fldname: # tokenize field name using _ tokens=fldname.split('_') for w in tokens: l = len(w) newW = w if l >= 2: newW = w[0].upper() + w[1:].lower() + ' ' elif l == 1: newW = w[0].upper() + ' ' else: newW = ''
retfldname += newW
# remove trailing space retfldname = retfldname[0:-1] return retfldname
