selFk = '''
select	fk.owner		FK_USER_NAME,
		fk.table_name	TABLE_NAME, 
		fk.constraint_name	FK_NAME, 
		pk.owner		PK_USER_NAME,	
		pk.table_name	PK_TABLE_NAME
From	all_constraints	fk,
		all_constraints	pk
where	fk.constraint_type	 ='R'
and	fk.owner 		= ?
and	fk.table_name 	= ?
and	pk.owner		= fk.r_owner
and	pk.constraint_name	= fk.r_constraint_name

'''

insFk = '''insert into SNP_REV_JOIN
	(I_MOD,
	FK_NAME,
	TABLE_NAME,
	FK_TYPE,
	PK_SCHEMA,
	PK_TABLE_NAME,
	CHECK_STAT,
	CHECK_FLOW,
	IND_ACTIVE)
values
	(
	/* I_MOD */			<%=snpRef.getModel("ID")%>,
	/* FK_NAME */		?,	
	/*TABLE_NAME*/		?,	
	/*FK_TYPE*/			'D',	
	/*PK_SCHEMA*/ 		? ,	
	/*PK_TABLE_NAME*/ 	?,	
	/*CHECK_STAT*/		'1',	
	/*CHECK_FLOW*/		'1',	
	/*IND_ACTIVE*/		'1'
			)
			'''
selFkCol = '''
		select	fk.table_name		FK_TABLE_NAME,
			pkc.table_name		PK_TABLE_NAME,
			fk.constraint_name		FK_NAME, 
			substr(fkc.column_name,1,35)	FK_COL_NAME,
			pkc.column_name		PK_COL_NAME,
			fkc.position			POS
		From	all_constraints	fk,
			all_constraints	pk,
			all_cons_columns	fkc,
			all_cons_columns	pkc
		where	fk.constraint_type 	= 'R'
		and	fk.owner		= ?
		and	fk.constraint_name 	= ?
		and	pk.owner		= fk.r_owner
		and	pk.constraint_name 	= fk.r_constraint_name
		and	pkc.owner 		= fk.r_owner
		and	pkc.constraint_name	= fk.r_constraint_name
		and	pkc.position 		= fkc.position
		and	fkc.owner 		= fk.owner
		and	fkc.constraint_name	= fk.constraint_name
'''

insFkCol = '''insert into SNP_REV_JOIN_COL
		(
		I_MOD,
		FK_NAME,
		FK_COL_NAME,
		FK_TABLE_NAME,
		PK_COL_NAME,
		PK_TABLE_NAME,
		POS
		)
	Values
		(
		/* I_MOD */				<%=snpRef.getModel("ID")%>,
		/* FK_NAME */			?,
		/* FK_COL_NAME */		?,
		/* FK_TABLE_NAME */		?,
		/* PK_COL_NAME */		?,
		/* PK_TABLE_NAME */		?,
		/* POS */				?
			)
			'''			

stFk = srcCx.prepareStatement  (selFk)
psFk = trgCx.prepareStatement(insFk) 

stFkCol = srcCx.prepareStatement  (selFkCol)
psFkCol = trgCx.prepareStatement(insFkCol) 

for tables in tableLst:
	stFk.clearParameters()
	stFk.setString(1,srcUser);
	stFk.setString(2,tables);
	rsFk = stFk.executeQuery()
	while rsFk.next():
		recordLog( "Foreign Key is %s from table %s to table %s" %(rsFk.getString("FK_NAME"),rsFk.getString("TABLE_NAME"),rsFk.getString("PK_TABLE_NAME")))
		nbFkeys +=1
		psFk.clearParameters()
		psFk.setString(1,rsFk.getString("FK_NAME"));
		psFk.setString(2,rsFk.getString("TABLE_NAME"));
		psFk.setString(3,rsFk.getString("FK_USER_NAME"));
		psFk.setString(4,rsFk.getString("PK_TABLE_NAME"));
		psFk.executeUpdate();

		stFkCol.clearParameters()
		stFkCol.setString(1,srcUser);
		stFkCol.setString(2,rsFk.getString("FK_NAME"));
		rsFkCol = stFkCol.executeQuery()
		while rsFkCol.next():
			recordLog( "\t%s.%s = %s.%s" %(rsFk.getString("TABLE_NAME"),rsFkCol.getString("FK_COL_NAME"),rsFkCol.getString("PK_TABLE_NAME"),rsFkCol.getString("PK_COL_NAME")))
			psFkCol.clearParameters()
			psFkCol.setString(1,rsFkCol.getString("FK_NAME"));
			psFkCol.setString(2,rsFkCol.getString("FK_COL_NAME"));
			psFkCol.setString(3,rsFkCol.getString("FK_TABLE_NAME"));
			psFkCol.setString(4,rsFkCol.getString("PK_COL_NAME"));
			psFkCol.setString(5,rsFkCol.getString("PK_TABLE_NAME"));
			psFkCol.setInt(6,rsFkCol.getInt("POS"));
			psFkCol.executeUpdate();
		rsFkCol.close();		

	rsFk.close();
psFk.close()
stFk.close();

selKey = '''
		Select	c.table_name	TABLE_NAME,
			c.constraint_name	KEY_NAME,
			Decode(c.constraint_type,'P','PK','U','AK')	CONS_TYPE
		From	all_constraints	c
		where	c.owner 		= ?
		and	c.table_name	= ?
		and	c.constraint_type	in ('P','U')
		union
		Select	table_name 	TABLE_NAME,
			index_name   KEY_NAME,
			'AK'	CONS_TYPE
		From	all_indexes
		where	
			TABLE_OWNER = ?
		and 	TABLE_NAME =  ?
		and	uniqueness	= 'UNIQUE'
		and	not exists 	(
				select 	'X' 
				from	all_constraints	c
				where	c.owner		= all_indexes.table_owner
				and	c.table_name	= all_indexes.table_name
				and	c.constraint_name	= all_indexes.index_name
				and	c.constraint_type	in ('U','P')
				)
'''


insKey = '''	insert into SNP_REV_KEY
				(
					I_MOD,
					TABLE_NAME,
					KEY_NAME,
					CONS_TYPE,
					IND_ACTIVE,
					CHECK_FLOW,
					CHECK_STAT
				)
				Values
				(
					/*I_MOD*/		<%=snpRef.getModel("ID")%>,
					/*TABLE_NAME*/	?,
					/*KEY_NAME*/	?,
					/*CONS_TYPE*/	?,
					/*IND_ACTIVE*/	'1',
					/*CHECK_FLOW*/	'1',
					/*CHECK_STAT*/	'1' 
				)
			'''
selKeyCol = '''
			Select 	column_name	COL_NAME,
					position		POS,
					TABLE_NAME	TABLE_NAME,
					CONSTRAINT_NAME KEY_NAME
			From	all_cons_columns
			where	owner			= ?
			and	constraint_name 	= ?
			union
			Select	
				column_name	,
				column_position	,
				TABLE_NAME,
				INDEX_NAME
			From	all_ind_columns
			where
			TABLE_OWNER		= ?
			and index_name 	= ?
'''	

insKeyCol = '''insert into SNP_REV_KEY_COL
			(
				I_MOD,
				TABLE_NAME,
				KEY_NAME,
				COL_NAME,
				POS
			)
			Values
			(
				/*I_MOD*/			<%=snpRef.getModel("ID")%>,
				/*TABLE_NAME*/		?,
				/*KEY_NAME*/		?,
				/*COL_NAME*/		?,
				/*POS*/				?
			)
			'''			

stKey = srcCx.prepareStatement  (selKey)
psKey = trgCx.prepareStatement(insKey) 

stKeyCol = srcCx.prepareStatement  (selKeyCol)
psKeyCol = trgCx.prepareStatement(insKeyCol) 
i=0
for tables in tableLst:
	stKey.clearParameters()
	stKey.setString(1,srcUser);
	stKey.setString(2,tables);
	stKey.setString(3,srcUser);
	stKey.setString(4,tables);

	rsKey = stKey.executeQuery()
	while rsKey.next():
		recordLog( "Key : %s %s" %(rsKey.getString("CONS_TYPE"),rsKey.getString("KEY_NAME")))
		nbKeys+=1
		psKey.clearParameters()
		psKey.setString(1,rsKey.getString("TABLE_NAME"));
		psKey.setString(2,rsKey.getString("KEY_NAME"));
		psKey.setString(3,rsKey.getString("CONS_TYPE"));

		psKey.executeUpdate();

		stKeyCol.clearParameters()
		stKeyCol.setString(1,srcUser);
		stKeyCol.setString(2,rsKey.getString("KEY_NAME"));
		stKeyCol.setString(3,srcUser);
		stKeyCol.setString(4,rsKey.getString("KEY_NAME"));
		rsKeyCol = stKeyCol.executeQuery()
		while rsKeyCol.next():
			recordLog( "\t%s" %rsKeyCol.getString("COL_NAME"))
			psKeyCol.clearParameters()
			psKeyCol.setString(1,rsKeyCol.getString("TABLE_NAME"));
			psKeyCol.setString(2,rsKeyCol.getString("KEY_NAME"));
			psKeyCol.setString(3,rsKeyCol.getString("COL_NAME"));
			psKeyCol.setInt(4,rsKeyCol.getInt("POS"));
			psKeyCol.executeUpdate();
		rsKeyCol.close();		

	rsKey.close();
psKey.close()
stKey.close();

recordLog( "\tNb Table \t\t%s" %nbTables)
