recordLog( "Reverse end ")

from oracle.AWXML import  *;
from oracle.AWAction import *;


awName = '<%=odiRef.getOption("AW_NAME")%>'
awUrl = '<%=odiRef.getOption("AW_URL")%>'
awOwner = '<%=odiRef.getOption("AW_OWNER")%>'
awPassword = '<%=odiRef.getOption("AW_PASSWORD")%>'
try:
	awConnection = AWConnection(awOwner, awPassword, awUrl);
	AW.readAWTopLevelDefinitions(awConnection, awName, "RO");

	for _cube in AW.getCurAW().getCubes():
		_cube.readAWDefinitions(awConnection, 1 == 1);
		for srcmap in _cube.getSourceMapGroup():
			for srcexp in srcmap.getMeasureMaps():
				for srccol in srcexp.getSourceColumns():
					Resultat = srccol.getColumn().split(".");
					tname = Resultat[0]+"."+Resultat[1];
					if (tableDic.has_key(tname)==0):
						tableDic[tname] ='FA';

		for V_DimRef in _cube.getCubeDimRefs():
			DimRef = V_DimRef.getDimension();
			DimRef.readAWDefinitions(awConnection, 1 == 1);
			for hie in DimRef.getHierarchies():
				for hieLevel in hie.getHierarchyLevels():
					for srcmap in hieLevel.getSourceMapGroup():
						for v_SrcCol in srcmap.getKeyMap().getSourceColumns():
							Resultat = v_SrcCol.getColumn().split(".");
							tname = Resultat[0]+"."+Resultat[1];
							if (tableDic.has_key(tname)==0):
								tableDic[tname] ='DI';

	awConnection.close();
except:
	raise "unable to connect to the Analytical Workspace", "Check the options, make sure that the awxml.jar exists in the drivers repository, make sure you have an AW in your schema."
